import React, { useEffect, useState } from 'react';
import axios from 'axios';

const NewImageDisplay = ({ type, number }) => {
  const [imageData, setImageData] = useState(null);
  const [imageNotFound, setImageNotFound] = useState(false);

  useEffect(() => {
    axios
      .get(`/api/v1/images/type/${type}/${number}`, { responseType: 'arraybuffer' })
      .then((response) => {
        const imageBlob = new Blob([response.data], { type: 'image/jpeg' });
        const imageUrl = URL.createObjectURL(imageBlob);
        setImageData(imageUrl);
      })
      .catch((error) => {
        console.error('Error retrieving image:', error);
        setImageNotFound(true);
      });

    return () => {
      if (imageData) {
        URL.revokeObjectURL(imageData);
      }
    };
  }, [type, number]);

  if (imageNotFound) {
    return <div>No image found</div>;
  }

  if (!imageData) {
    return null; // Display nothing until the image retrieval is complete
  }

  return <img src={imageData} alt="Image" className="smallimg" />;
};

export default NewImageDisplay;
