import React, { useState, useEffect } from 'react';
import NewImageDisplay from './NewImageDisplay';
import { Modal, Button, Carousel } from 'react-bootstrap';

function BedRoom({ room }) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [imageCount, setImageCount] = useState(1);

  useEffect(() => {
    const checkImageExistence = async () => {
      let exists = true;
      let count = 1;

      while (exists) {
        try {
          await fetch(`/api/v1/images/type/bedroom/${count}`);
          count++;
        } catch (error) {
          exists = false;
        }
      }

      setImageCount(count - 1);
    };

    checkImageExistence();
  }, []);

  const renderImages = () => {
    const images = [];
    for (let i = 1; i <= imageCount; i++) {
      images.push(
        <Carousel.Item key={i}>
          <NewImageDisplay type="bedroom" number={i} />
        </Carousel.Item>
      );
    }
    return images;
  };

  return (
    <div className="row boxshadow">
      <div className="col-md-4">
        {renderImages()}
      </div>
      <div className="col-md-7">
        <h1>Room number {room.number}</h1>
        <h5>Beds count: {room.bedsCount}</h5>
        <h5>Tvs count: {room.tvsCount}</h5>
        <h5>Color: {room.color}</h5>
        <p>Details: {room.details}</p>

        <div style={{ float: 'right' }}>
          <button className="btn btn-primary" onClick={handleShow}>View details</button>
        </div>
      </div>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Room number {room.number}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Carousel>{renderImages()}</Carousel>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default BedRoom;
