function Review({ review }) {

    const [user, setUser] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const responseUser = await axios.get(`/api/v1/users/${review.idUser}`);
                const user = responseUser.data;
                setUser(user);
            } catch (error) {
                console.log('Error getting a user:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, []);

