import React, { useState } from 'react';
import NewImageDisplay from './NewImageDisplay';
import { Modal, Button, Carousel } from 'react-bootstrap';
import ImageUploadForm from './ImageUploadForm';

function AdminBedRoom({ room }) {
    const [number, setNumber] = useState(false);
    const [bedsCount, setBedsCount] = useState(false);
    const [tvsCount, setTvsCount] = useState(false);
    const [color, setColor] = useState(false);
    const [details, setDetails] = useState(false);
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [showModify, setShowModify] = useState(false);
    const handleCloseModify = () => setShowModify(false);
    const handleShowModify = () => setShowModify(true);

    const imageCount = 3; // Define the maximum number of images to retrieve (adjust as needed)

    const renderImages = () => {
        const images = [];
        for (let i = 1; i <= imageCount; i++) {
            images.push(
                <Carousel.Item key={i}>
                    <NewImageDisplay type={"bedroom" + room.number} number={i} styling={"bigimg"} />
                </Carousel.Item>
            );
        }
        return images;
    };

    const handleModify = () => {
        
    };

    return (
        <div className="row boxshadow">
            <div className="col-md-4">
                <NewImageDisplay type={"bedroom" + room.number} number={1} styling={"smallimg"} /> {/* Pass the necessary props */}
            </div>
            <div className="col-md-7">
                <h1>Room number {room.number}</h1>
                <h5>Beds count: {room.bedsCount}</h5>
                <h5>Tvs count: {room.tvsCount}</h5>
                <h5>Color: {room.color}</h5>

                <div style={{ float: 'right' }}>
                    <button className="btn btn-primary" onClick={handleShow}>View details</button>
                    <button className="btn btn-primary" onClick={handleShowModify}>Modify details</button>
                </div>
            </div>

            <Modal show={show} onHide={handleClose} size='lg'>
                <Modal.Header>
                    <Modal.Title>Room number {room.number}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Carousel>{renderImages()}</Carousel>
                    <p>Details: {room.details}</p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
            <Modal show={show} onHide={handleClose} size='lg'>

            <Modal.Header>
                <Modal.Title>Modify Room number {room.number}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div>
                    <div className='row justify-content-center mt-5'>
                        <div>
                            <div>
                                <h3>Complete the following data:</h3>
                                <input type="text" className='form-control mt-1 inputstyling' placeholder='number'
                                    value={number} onChange={(e) => { setNumber(e.target.value) }} />
                                <input type="text" className='form-control mt-1 inputstyling' placeholder='bedsCount'
                                    value={bedsCount} onChange={(e) => { setBedsCount(e.target.value) }} />
                                <input type="tvsCount" className='form-control mt-1 inputstyling' placeholder='tvsCount'
                                    value={tvsCount} onChange={(e) => { setTvsCount(e.target.value) }} />
                                <input type="color" className='form-control mt-1 inputstyling' placeholder='color'
                                    value={tvsCount} onChange={(e) => { setColor(e.target.value) }} />
                                <input type="details" className='form-control mt-1 inputstyling' placeholder='details'
                                    value={tvsCount} onChange={(e) => { setDetails(e.target.value) }} />
                                <ImageUploadForm />
                            </div>
                        </div>
                    </div>
                </div>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
                <Button variant="secondary" onClick={handleModify}>
                    Update
                </Button>
            </Modal.Footer>
            </Modal>
        </div>
    );
}

export default AdminBedRoom;
