import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ImageDisplay({ imageId }) {
  const [imageData, setImageData] = useState(null);

  useEffect(() => {
    axios.get(`/api/v1/images/${imageId}`, { responseType: 'arraybuffer' })
      .then((response) => {
        const imageBlob = new Blob([response.data], { type: 'image/jpeg' }); // Adjust the image type if needed
        const imageUrl = URL.createObjectURL(imageBlob);
        setImageData(imageUrl);
      })
      .catch((error) => {
        console.error('Error retrieving image:', error);
        // Handle image retrieval error
      });

    // Clean up the URL.createObjectURL when component unmounts
    return () => {
      if (imageData) {
        URL.revokeObjectURL(imageData);
      }
    };
  }, [imageId]);

  if (!imageData) {
    return <div>Loading...</div>;
  }

  return (
    <img src={imageData} alt="Image" />
  );
}

export default ImageDisplay;
