import React, { useState } from 'react';
import NewImageDisplay from './NewImageDisplay';
import { Modal, Button, Carousel } from 'react-bootstrap';

function Bathroom({ bathroom, houseId }) {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const imageCount = 3; // Define the maximum number of images to retrieve (adjust as needed)

    const renderImages = () => {
        const images = [];
        for (let i = 1; i <= imageCount; i++) {
            images.push(
                <Carousel.Item key={i}>
                    <NewImageDisplay type={"bathroom" + bathroom.number + bat} number={i} styling={"bigimg"}/>
                </Carousel.Item>
            );
        }
        return images;
    };

    return (
        <div className="row boxshadow">
            <div className="col-md-4">
                <NewImageDisplay type={"bathroom" + bathroom.number} number={1} styling={"smallimg"}/> {/* Pass the necessary props */}
            </div>
            <div className="col-md-7">
                <h1>Bathroom number {bathroom.number}</h1>
                <h5>Size: {bathroom.size}</h5>
                <h5>Mirrors count: {bathroom.mirrorsCount}</h5>
                <h5>Shower: {bathroom.shower}</h5>
                <h5>Bathtub: {bathroom.bathtub}</h5>
                <h5>WC: {bathroom.wc}</h5>  
                <h5>Sink: {bathroom.sink}</h5>    

                <div style={{ float: 'right' }}>
                    <button className="btn btn-primary" onClick={handleShow}>View details</button>
                </div>
            </div>

            <Modal show={show} onHide={handleClose} size='lg'>
                <Modal.Header>
                    <Modal.Title>Bathroom number {bathroom.number}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Carousel>{renderImages()}</Carousel>
                    <p>Details: {bathroom.details}</p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default Bathroom;
