import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import Review from '../components/Review';
import NewImageDisplay from '../components/NewImageDisplay';

function HouseDetails() {
    const [loading, setLoading] = useState();
    const [error, setError] = useState();
    const [reviews, setReviews] = useState([]);
    const [house, setHouse] = useState();


    const { houseid } = useParams();

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const responseReviews = await axios.get(`/api/v1/reviews/${houseid}`);
                const reviews = responseReviews.data;
                setReviews(reviews);
                const responseHouse = await axios.get('/api/v1/houses');
                const house = responseHouse.data;
                setHouse(house);
                setLoading(false);
            } catch (error) {
                setError(true);
                console.log('Error fetching rooms:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    return (
        <div>
            <div className='row'>
                <div className='col-md-5'>
                    <h1>{house.name}</h1>
                    <NewImageDisplay type={house.name} number={1} styling={"bigimg"} />
                </div>
                <div className='col-md-5'>

                </div>

            </div>

            <Link to={`/bedrooms/${houseid}`}>
                <button className='btn btn-primary m-2'>View rooms</button>
            </Link>
            <div className='row justify-content-center mt-5'>
                {loading ? (<h1>Loading...</h1>) : error ? (<h1>Error</h1>) : (reviews.map(review => {
                    return <div className='col-md-9 mt-2'>
                        <Review review={review} />
                    </div>

                }))}
            </div>
        </div>
    )
}

export default HouseDetails;
