import React, { useEffect, useState } from 'react'
import { Tabs } from "antd"
import axios from 'axios';

const { TabPane } = Tabs;
function AdminScreen() {
    return (
        <div className='mt-3 m-3 boxshadow'>
            <h1 className='text-center' style={{ fontSize: '30px' }}><b>Admin panel</b></h1>
            <Tabs defaultActiveKey="1" >
                <TabPane tab="Bookings" key="1">
                    <Bookings />
                </TabPane>
                <TabPane tab="Rooms" key="2">
                    <h1>Rooms</h1>
                </TabPane>
                <TabPane tab="Add Room" key="3">
                    <h1>Add Room</h1>
                </TabPane>
                <TabPane tab="Bathrooms" key="4">
                    <h1>Bathrooms</h1>
                </TabPane>
                <TabPane tab="Add Bathroom" key="5">
                    <h1>Add Bathroom</h1>
                </TabPane>
                <TabPane tab="Clients" key="6">
                    <h1>Clients</h1>
                </TabPane>
            </Tabs>
        </div>
    )
}

export default AdminScreen

export function Bookings() {

    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState();

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const responseBookings = await axios.get('/api/v1/reservations');
                const bookings = responseBookings.data;
                setBookings(bookings);
                setLoading(false);
            } catch (error) {
                setError(true);
                console.log('Error fetching bookings:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    return (
        <div className='row'>
            <div className='col-md-10'>
                <table className='table table-border table-dark'>
                    <thead className='boxshadow'>
                        <tr>
                            <th>Booking Id</th>
                            <th>User Id</th>
                            <th>Client Id</th>
                            <th>Start date</th>
                            <th>End date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {loading ? (<h1>Loading...</h1>) : error ? (<h1>Error</h1>) : (bookings.map(booking => {
                            return <tr>
                                <p>Salut</p>
                            </tr>

                        }))}
                    </tbody>
                </table>
            </div>
        </div>
    )
}
